class App
{
    get(u, c, e)
    {
        axios.get(u)
        .then(r => {
            if('function' === typeof c)
                c(r)
        })
        .catch(r => {
            if('function' === typeof e)
                e(r) 
        })
    }

    post(u, d, c, e)
    {
        axios.post(u, d)
        .then(r => {
            if('function' === typeof c)
                c(r)
        })
        .catch(r => {
            if('function' === typeof e)
                e(r) 
        })
    }

    modal(data)
    {
        const template = `
            <dialog class="ui-dialog ui-dialog--${data?.size ? data.size : 'md'}" ${data?.id ? 'id="'+data.id+'"' : ''}>
                ${data?.title ? '<div data-ui-dialog-title class="ui-dialog-title">'+data.title+'</div>' : ''}
                ${data?.content ? `
                <div data-ui-dialog-description>
                    ${data.content}
                </div>
                ` : ``}
                <button data-ui-dismiss class="ui-btn-close ui-dialog-close" aria-label="Close"></button>
            </dialog>
        `;

        return new Promise((resolve) => {
            new jolty.Dialog(template, {
                autodestroy: true
            });
        });
    }

    dialog(e, t)
    {
        switch(t)
        {
            case 'toggle': case 't':
                jolty.Dialog.toggle(e)
                break;

            case 'hide': case 'h':
                jolty.Dialog.hide(e)
                break;

            default:
                jolty.Dialog.show(e)
        }
    }
}

const app = new App()